class Solution:
    def longestPalindrome(self, s):
        """
        :type s: str
        :rtype: str
        """
        memo = {}
        
        originalLen = len(s)
        if  originalLen <=1 :
            return s
        

        foundValue = s[0]
        start = 0
        end = 0
        for i in range(originalLen):
            memo[i,i] =True
            if (i + 1 < originalLen):
                if s[i]==s[i+1]:
                    memo[i,i+1] = True
                    foundValue = s[i:i+1]
                    start = i
                    end = i+1
                    
        print(foundValue)
          
        for leng in range(3, originalLen+1):
            for i in range(originalLen):
                j = i + leng -1
                if (j < originalLen and memo.get((i+1,j-1)) and s[i]==s[j]):
                    memo[i,j] = True
                    start = i
                    end = j
                
        return s[start:end+1]



